const arg = require('arg');
const chalk = require('chalk');
const { stdout, stdin } = require('process');

function parseArgs(rawArgs) {
    const args = arg(
        {
            '--help': Boolean,
        },
        {
            argv: rawArgs.slice(2),
        }
    );
    return {
        help: args['--help'] || false,
        operation: args._[0],
        type: args._[1],
        component: args._[2],
    }
}

